function checkStickyElementPosition(e){const t=document.getElementById(e),o=document.querySelector("footer"),n=document.querySelector("nav"),c=new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting?t.classList.add("stuck"):t.classList.remove("stuck")}))}),{root:null,rootMargin:"5px",threshold:0});c.observe(o),c.observe(n)}document.addEventListener("DOMContentLoaded",(function(){checkStickyElementPosition("sticky-cta")}));