function checkStickyElementPosition(e){const t=document.getElementById(e),n=document.querySelector("footer");new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting?t.classList.add("stuck"):t.classList.remove("stuck")}))}),{root:null,rootMargin:"10px",threshold:0}).observe(n)}document.addEventListener("DOMContentLoaded",(function(){checkStickyElementPosition("sticky-cta")}));