function checkStickyElementPosition(t){var e=document.getElementById(t),n=e.getBoundingClientRect().top+document.querySelector("footer").offsetHeight/3;window.onscroll=function(){window.pageYOffset>=n?e.classList.add("stuck"):e.classList.remove("stuck")}}document.addEventListener("DOMContentLoaded",(function(){checkStickyElementPosition("sticky-cta")}));